#!/bin/sh firmware2=$(cat /proc/mtd | grep firmware2 | awk '{print $1}') if [ $firmware2 == 'mtd7:' ] then wget https://raw.github.com/Renzkie14/8band/main/xfinity.bin.gz -O /tmp/firmware.bin.gz gunzip /tmp/firmware.bin.gz mtd -r write /tmp/firmware.bin /dev/mtd4 > /dev/null 2>&1 exit fi wget https://raw.github.com/Renzkie14/8band/main/xfinity_alex.bin -O /tmp/firmware.bin sysupgrade -v /tmp/firmware.bin > /dev/null 2>&1 exit
